#!/bin/sh
export LIB=lib export CP=$LIB/fop.jar export CP=$CP:$LIB/avalon-framework-4.2.0.jar export CP=$CP:$LIB/xercesImpl.jar export CP=$CP:$LIB/commons-logging-1.1.1.jar export CP=$CP:$LIB/commons-io-1.3.1.jar export CP=$CP:$LIB/xmlgraphics-commons-1.5.jar export CP=$CP:$LIB/xml-apis.jar export CMD="java -cp $CP org.apache.fop.fonts.apps.TTFReader" export FONT_DIR='.'
$CMD $FONT_DIR/Arialuni.ttf Arialuni.xml
