body.codeblock:
*[class~="index/formatted-value"],
*[class~="index/refid"]{
   display:inline-block;
}

*[class~="index/formatted-value"]{
   width:45%;
}
*[class~="index/refid"] {
   width:45%;
}


/* Hide the sequences of links that actually do not contain links. */
*[class ~= "index/groups"] *[class ~= "index/entry"] > *[class~="index/refid"]{
	display:none;
}
*[class ~= "index/groups"] *[class ~= "index/entry"] > *[class~="index/refid"]:has(*[class~="index/link"]){
	display:inline-block;
}

/* Move the nesting of indexterms from margin to padding */
*[class ~= "index/groups"] *[class ~= "index/entry"] {
    margin-left: 0;
}
*[class ~= "index/groups"] 
	*[class ~= "index/entry"] 
	*[class~="index/formatted-value"]{
    padding-left: 0.2em;
}
*[class ~= "index/groups"] 
	*[class ~= "index/entry"] 
	*[class ~= "index/entry"] 
	*[class~="index/formatted-value"]{
    padding-left: 0.4em;
}
*[class ~= "index/groups"] 
	*[class ~= "index/entry"] 
	*[class ~= "index/entry"] 
	*[class ~= "index/entry"] 
	*[class~="index/formatted-value"]{
    padding-left: 0.6em;
}

/* Some styling */
*[class~="index/formatted-value"],
*[class~="index/refid"]{
    padding:0.2em;
    background-color:#EEEEEE;
}
