body.codeblock:
@page :left {
     @top-left {
         content: string(maptitle) string(parttitle) string(chaptertitle) string(sectiontitle) " | " counter(page);
     }
 }

 @page :right{
     @top-right {
         content: string(maptitle) string(parttitle) string(chaptertitle) string(sectiontitle) " | " counter(page);
     }
 }

@page :left {
     @top-left {
         content:none;
     }
     @top-left-corner {
         content: string(maptitle) string(parttitle) string(chaptertitle) string(sectiontitle) " | " counter(page);
         white-space: nowrap;	
         text-align:left;
     }
 }

 @page :right{
     @top-right {
         content: none;
     }
     @top-right-corner {
         content: string(maptitle) string(parttitle) string(chaptertitle) string(sectiontitle) " | " counter(page);
         white-space: nowrap;	
         text-align:right;
     }
 }
